# Fonction pour lire les événements depuis le fichier CSV
def lire_evenements(fichier_csv):
    evenements = []
    with open(fichier_csv, newline='', encoding='utf-8') as csvfile:
        lecteur = csv.DictReader(csvfile)
        for ligne in lecteur:
            evenements.append(ligne)
    return evenements

