mois_a = ["janvier", "février", "mars", "avril", "mai", "juin", "juillet", "août", "septembre", "octobre", "novembre", "décembre"]

# Année actuelle et suivante
annee_actu = datetime.now().year
annee_suiv = annee_actu + 1

# Générer une liste des mois pour l'année en cours et l'année suivante
