def CalendrierEvenement(request):
    csv_file_path = os.path.join(settings.BASE_DIR, 'ListeEvenements.csv')
    events = []
    
    # Récupérer les filtres
    #date_debut_filter = request.GET.get('date_debut')
    type_evenement_filter = request.GET.get('type')
    mois_annee_filter = request.GET.get('mois')

    with open(csv_file_path, mode='r', encoding='utf-8') as csvfile:
        reader = csv.DictReader(csvfile)
        for row in reader:
            if row['Afficher'] == '1':
                date_debut = datetime.strptime(row['Date de début'], '%d/%m/%Y')
                mois = mois_a[date_debut.month - 1]  # Récupérer le mois sous forme de texte
                annee = date_debut.year

                # Filtrage par mois et année
                if mois_annee_filter and (mois + ' ' + str(annee)) != mois_annee_filter:
                    continue

                # Filtrage par type
                if type_evenement_filter and row['Type'] != type_evenement_filter:
                    continue

                # Gérer l'affichage du logo en fonction de l'enseigne
                enseigne = row['Enseigne']
                if enseigne in Dic_enseigne:
                    lien_logo = Dic_enseigne[enseigne]  # Utiliser le logo de l'enseigne
                else:
                    lien_logo = row['Lien du logo']  # Sinon, utiliser le logo par défaut

                # Ajouter l'événement à la liste
                events.append({
                    'date_debut': row['Date de début'],
                    'date_fin': row['Date de fin'],
                    'nom': row['Nom'],
                    'adresse': row['Adresse'],
                    'type': row['Type'],
                    'lien_logo': lien_logo,  # Utiliser le lien du logo correct
                    'lien': row['Lien'],
                    'Afficher': row['Afficher']
                })

    # Trier les événements par date de début
    events = sorted(events, key=lambda event: datetime.strptime(event['date_debut'], '%d/%m/%Y'))

    # Pagination
    page = request.GET.get('page', 1)
    paginator = Paginator(events, 5)  # 5 événements par page

    try:
        events_paginated = paginator.page(page)
    except PageNotAnInteger:
        events_paginated = paginator.page(1)
    except EmptyPage:
        events_paginated = paginator.page(paginator.num_pages)

    today = datetime.today().strftime('%Y-%m-%d')
    return render(request, 'CalendrierEvenement.html', {
        'events': events_paginated,
        'date_debut': today,
        'type_evenement': type_evenement_filter,
        'mois_annees': mois_annees,
        'mois_selectionne': mois_annee_filter
    })


