# Vue pour ajouter un événement

def ajouter_evenement(request):
    if request.method == 'POST':
        form = EvenementForm(request.POST)
        if form.is_valid():
            # Création d'un événement sans sauvegarde immédiate
            evenement = form.save(commit=False)

            # Vérification de l'unicité du nom
            nom_existe = False
            with open('ListeEvenements.csv', mode='r') as file:
                reader = csv.reader(file)
                for row in reader:
                    if row[1] == evenement.nom:  # Supposant que le nom est en 2e colonne
                        nom_existe = True
                        break

            if nom_existe:
                form.add_error('nom', "Un événement avec ce nom existe déjà.")
                return render(request, 'ajouter_evenement.html', {'form': form})


            # Gestion de l'enseigne et du logo
            # Dans views.py
            if evenement.enseigne != "Autre":
                evenement.lien_logo = None  # Champ vide si ce n'est pas 'Autre'
            
            logo_url = form.cleaned_data.get('lien_logo')
            logo_base64 = None  # Initialise la variable pour le CSV
            if logo_url:
                try:
                    response = requests.get(logo_url)
                    if response.status_code == 200:
                        # Redimensionnement de l'image si le lien est valide
                        img = Image.open(BytesIO(response.content))
                        new_height = int(img.height * 100 / img.width)
                        img = img.resize((100, new_height), Image.Resampling.LANCZOS)
            
                        # Convertir l'image redimensionnée en Base64 avec le préfixe nécessaire
                        buffer = BytesIO()
                        img.save(buffer, format="PNG")
                        logo_base64 = "data:image/png;base64," + base64.b64encode(buffer.getvalue()).decode('utf-8')
                        evenement.lien_logo = logo_base64
                except requests.exceptions.RequestException:
                    form.add_error('lien_logo', "Le lien du logo est invalide.")
                    return render(request, 'ajouter_evenement.html', {'form': form})


            # Enregistrement final de l'événement
            evenement.save()

            # Enregistrement dans le CSV
            with open('ListeEvenements.csv', mode='a', newline='') as file:
                writer = csv.writer(file)
                writer.writerow([
                    0,
                    form.cleaned_data['nom'],
                    form.cleaned_data['lien'],
                    form.cleaned_data['adresse'],
                    form.cleaned_data['date_debut'].strftime('%d/%m/%Y'),
                    form.cleaned_data['date_fin'].strftime('%d/%m/%Y'),
                    form.cleaned_data['type_evenement'],
                    form.cleaned_data['enseigne'],
                    logo_base64 if logo_url else None,
                    form.cleaned_data['commentaire']
                ])

            return render(request, 'Confirmation.html', {'event_name': form.cleaned_data['nom']})
    else:
        form = EvenementForm()

    return render(request, 'ajouter_evenement.html', {'form': form})

